package Practice;

public class TypeCasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Implicit Type Casting
		int a=10;
		float f=a;
		System.out.println(f);
	
		//Explicit Type Casting
		double d=12.3;
		int i=(int)d;
		System.out.println(i);
		 int b=90;
		char c=(char)b;
		System.out.println(c);
		
		

	}

}
